# =======================
# 2) 通用解析函数：从字符串里提取 62 维平均选择概率
# =======================
def parse_selection_string(s: str, n_channels: int = 62):
    lines = s.splitlines()
    selected_lines = [ln for ln in lines if 'selected channels' in ln]
    all_arrays = []
    for ln in selected_lines:
        m = re.search(r'\[(.*?)\]', ln)
        if not m:
            continue
        # 解析逗号分隔的索引
        idx_str = m.group(1)
        if idx_str.strip() == "":
            continue
        idxs = [int(x.strip()) for x in idx_str.split(',')]
        # 去重并裁边
        idxs = [i for i in set(idxs) if 0 <= i < n_channels]
        arr = np.zeros(n_channels, dtype=float)
        arr[idxs] = 1.0
        all_arrays.append(arr)
    if len(all_arrays) == 0:
        print("警告：未从该字符串中解析到 'selected channels' 行。返回全零。")
        return np.zeros(n_channels, dtype=float), 0
    all_arrays = np.stack(all_arrays, axis=0)   # [num_subjects, 62]
    avg = all_arrays.mean(axis=0)               # [62]
    return avg, all_arrays.shape[0]

# 解析 single / cross
avg_single, n_sub_single = parse_selection_string(str_single, n_channels=62)
avg_cross,  n_sub_cross  = parse_selection_string(str_cross,  n_channels=62)

print(f"[Single] 解析到 {n_sub_single} 个被试；[Cross] 解析到 {n_sub_cross} 个被试。")

# =======================
# 3) 读取自定义电极坐标 → MNE montage / info
#    注意：此处通道顺序必须与你索引(0..61)一致
# =======================
sensor_dataframe = pd.read_excel('custom_sensor_dataframe.xlsx', index_col=0)
channels1020 = np.array(sensor_dataframe.index)   # ["Fp1","Fp2",...]
coords1020   = np.array(sensor_dataframe)         # (62, 3)
ch_pos_dict  = dict(zip(channels1020, coords1020))

montage = mne.channels.make_dig_montage(
    ch_pos=ch_pos_dict,
    nasion=[5.27205792e-18, 8.60992398e-02, -4.01487349e-02],
    lpa=[-0.08609924, -0.0, -0.04014873],
    rpa=[0.08609924,  0.0, -0.04014873],
)
info = mne.create_info(ch_names=montage.ch_names, sfreq=128., ch_types='eeg')

# MNE 需要 (n_channels, n_times) 形状
evk_single = mne.EvokedArray(avg_single.reshape(len(montage.ch_names), 1), info.copy())
evk_single.set_montage(montage)
evk_cross  = mne.EvokedArray(avg_cross.reshape(len(montage.ch_names), 1), info.copy())
evk_cross.set_montage(montage)

# =======================
# 4) 画并排两个 topomap，共享色标 [0,1]
# =======================

label_thresh = 0.5
names_single = [ch if v >= label_thresh else "" for ch, v in zip(channels1020, avg_single)]
names_cross  = [ch if v >= label_thresh else "" for ch, v in zip(channels1020, avg_cross)]

def plot_one(ax, values, evk_info, names, title):
    # 某些 MNE 版本没有 extrapolate 参数，这里做个兼容
    try:
        im, cn = mne.viz.plot_topomap(
            data=values,
            pos=evk_info,
            axes=ax,
            show=False,
            outlines='head',
            # contours=0,
            names=names,  # 只显示 >= 阈值的标签
        )
    except TypeError:
        im, cn = mne.viz.plot_topomap(
            data=values,
            pos=evk_info,
            axes=ax,
            show=False,
            outlines='head',
            # contours=0,
            names=names,
        )
    im.set_clim(0.0, 1.0)  # 统一色标
    ax.set_title(title, fontsize=10)
    return im

fig = plt.figure(figsize=(8.2, 4.0), constrained_layout=False)
gs = fig.add_gridspec(nrows=1, ncols=2, width_ratios=[1, 1], wspace=0.15)

ax1 = fig.add_subplot(gs[0, 0])
ax2 = fig.add_subplot(gs[0, 1])
# cax = fig.add_subplot(gs[0, 2])   # 专用色条轴

# 画两张 topomap（保持你原来的 plot_one 函数不变）
im1 = plot_one(ax1, avg_single, evk_single.info, names_single, "(a) Within-subject setting")
im2 = plot_one(ax2, avg_cross,  evk_cross.info,  names_cross,  "(b) Cross-subject setting")

# 统一色标范围
im1.set_clim(0.0, 1.0)
im2.set_clim(0.0, 1.0)

# 关键：手动创建一个“矮一点”的 colorbar 轴
pos2 = ax2.get_position()  # 以右边这幅图的位置为参考
cax = fig.add_axes([
    pos2.x1 + 0.02,               # x 位置（在 ax2 右边一点）
    pos2.y0 + 0.1 * pos2.height,  # y 位置（往上移一点，留上下空白）
    0.02,                         # 宽度
    0.9 * pos2.height             # 高度：只用 90%，比 topomap 矮
])

# 把色条画在 cax 上
cb = fig.colorbar(im2, cax=cax)
cb.set_label('Selection  Probability', fontsize=9)
cb.ax.tick_params(labelsize=7)

fig.tight_layout()  # 或者省略；有了 GridSpec 基本不会挤
